import { Component, computed, signal } from '@angular/core';
import { SportsDataApiService } from 'src/api/services/sports-data-api.service';
import { toObservable, toSignal } from '@angular/core/rxjs-interop';
import { switchMap } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})
export class AppComponent {

  protected date = signal(new Date());

  protected games = toSignal(toObservable(this.date).pipe(
    switchMap(date => {
      const data = { date: this.date().toISOString() };

      return this.sportsDataApiService.apiSportsDataApiScoresByDateGet$Json(data)
    })
  ));

  constructor(private sportsDataApiService: SportsDataApiService) { }
}
<h1 class="font-bold text-xs">
  @for (game of games(); track game.gameID) {
  <div class="game-card bg-white rounded-lg shadow-lg p-6">
    <h2 class="text-2xl font-bold mb-4">{{ game.homeTeam }} vs {{ game.awayTeam }}</h2>
    <p class="text-gray-600 mb-2">Date: {{ game.dateTimeUTC | date:'longDate' }}</p>
        <p class="text-gray-600 mb-2">Time: {{ game.dateTimeUTC + 'Z' | date:'shortTime' }}</p>
        <p class="text-gray-600 mb-2">Date: {{ game.dateTimeUTC + 'Z' | date:'longDate' }}</p>
        <!-- <p class="text-gray-600 mb-2">Location: {{ game. }}</p> -->
        <p class="text-gray-600 mb-2">Score: {{ game.homeTeamScore }} - {{ game.awayTeamScore }}</p>
      </div>
      }
    </h1>
